import "./css/mainpage.css";
import React, { useRef, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";

import { Pagination, Autoplay } from "swiper/modules";
import "swiper/css";
import "swiper/css/pagination";

export default function Mainpage() {
  // swiper pause/play
  const swiperRef = useRef(null);

  const handlePlay = () => {
    if (swiperRef.current && swiperRef.current.swiper) {
      swiperRef.current.swiper.autoplay.start();
    }
  };

  const handlePause = () => {
    if (swiperRef.current && swiperRef.current.swiper) {
      swiperRef.current.swiper.autoplay.stop();
    }
  };
  return (
    <div className="Mainpage">
      <div className="mainpageSwiperWrapper">
        <Swiper
          ref={swiperRef}
          pagination={{ clickable: true }}
          modules={[Pagination, Autoplay]}
          loop={true}
          autoplay={{
            delay: 3000,
            disableOnInteraction: true,
          }}
          className="mySwiper"
        >
          <SwiperSlide>
            <div className="mainSwiperList">
              <p class="big">
                월 최대&nbsp;3만원 할인!
                <br />
                모니모카드&nbsp;혜택
              </p>
              <p class="small">
                편의점,&nbsp;온라인쇼핑몰 등<br />
                필요한 혜택만 모두&nbsp;모았어요!
              </p>
              {/* <img src="./mainSwiper01.png" alt="" /> */}
            </div>
          </SwiperSlide>
          <SwiperSlide>
            <img src="./mainSwiper02.png" alt="" />
          </SwiperSlide>
          <SwiperSlide>
            <img src="./mainSwiper03.png" alt="" />
          </SwiperSlide>
          <SwiperSlide>
            <img src="./mainSwiper04.png" alt="" />
          </SwiperSlide>
          <SwiperSlide>
            <img src="./mainSwiper05.png" alt="" />
          </SwiperSlide>
          <SwiperSlide>
            <img src="./mainSwiper06.png" alt="" />
          </SwiperSlide>
        </Swiper>
      </div>
      <button onClick={handlePlay}>Play</button>
      <button onClick={handlePause}>Pause</button>
    </div>
  );
}
